import React from "react";
import { Container } from "react-bootstrap";

const Summary = () => (
  <section id="summary" className="py-5 bg-white">
    <Container>
      <h2 className="mb-4">Professional Summary</h2>
      <p>
        I'm an insurance and risk management professional with over <strong>10 years of hands-on experience</strong> in underwriting, claims handling, and operational coordination. 
        Throughout my career at organizations like Muang Thai Insurance, Tokio Marine, and Kerry Siam Seaport, I've had the opportunity to work alongside dedicated teams and support complex insurance processes.
      </p>
      <p>
        With a growing interest in technology and data, I've started applying tools like <strong>React</strong>, <strong>Flask</strong>, and <strong>BigQuery</strong> to improve workflows and decision-making in the insurance space.
        Currently, I’m developing <strong>Cheetah Insurance Broker</strong>, a platform aimed at simplifying car insurance comparison and purchase experiences — all while continuing to learn and grow in the field.
      </p>
    </Container>
  </section>
);

export default Summary;
